use ethcontract::{Address, H256};
use reqwest::{
    header::{HeaderMap, HeaderValue},
    Client, Url,
};
use serde::{Deserialize, Serialize};
use web3::types::Bytes;

#[derive(Debug, Clone, Serialize)]
pub struct TenderlyRequest {
    pub network_id: String,
    pub block_number: u64,
    pub from: Address,
    pub input: Bytes,
    pub to: Address,
    pub generate_access_list: bool,
}

#[derive(Debug, Clone, Deserialize)]
pub struct TenderlyResponse {
    pub generated_access_list: Vec<AccessListItem>,
}

// Had to introduce copy of the web3 AccessList because tenderly responds with snake_case fields
// and tenderly storage_keys field does not exist if empty (it should be empty Vec instead)
#[derive(Debug, Clone, Deserialize)]
pub struct AccessListItem {
    /// Accessed address
    pub address: Address,
    /// Accessed storage keys
    pub storage_keys: Option<Vec<H256>>,
}

impl From<AccessListItem> for web3::types::AccessListItem {
    fn from(item: AccessListItem) -> Self {
        Self {
            address: item.address,
            storage_keys: item.storage_keys.unwrap_or_default(),
        }
    }
}

#[derive(Debug)]
pub struct TenderlyApi {
    url: Url,
    client: Client,
    header: HeaderMap,
}

impl TenderlyApi {
    #[allow(dead_code)]
    pub fn new(url: Url, api_key: &str) -> Self {
        Self {
            url,
            client: Client::new(),
            header: {
                let mut header = HeaderMap::new();
                header.insert("x-access-key", HeaderValue::from_str(api_key).unwrap());
                header
            },
        }
    }

    #[allow(dead_code)]
    pub async fn access_list(&self, body: TenderlyRequest) -> reqwest::Result<TenderlyResponse> {
        self.client
            .post(self.url.clone())
            .headers(self.header.clone())
            .json(&body)
            .send()
            .await?
            .error_for_status()?
            .json()
            .await
    }
}

#[cfg(test)]
mod tests {
    use super::*;
    use ethcontract::H160;
    use hex_literal::hex;

    #[tokio::test]
    #[ignore]
    async fn real_request() {
        let tenderly_api = TenderlyApi::new(
            // http://api.tenderly.co/api/v1/account/<USER_NAME>/project/<PROJECT_NAME>/simulate
            Url::parse(&std::env::var("TENDERLY_URL").unwrap()).unwrap(),
            &std::env::var("TENDERLY_API_KEY").unwrap(),
        );
        let request = TenderlyRequest {
            network_id: "1".to_string(),
            block_number: 14122310,
            from: H160::from_slice(&hex!("e92f359e6f05564849afa933ce8f62b8007a1d5d")),
            input: hex!("").into(),
            to: H160::from_slice(&hex!("9008d19f58aabd9ed0d60971565aa8510560ab41")),
            generate_access_list: true,
        };
        let access_list = tenderly_api.access_list(request).await.unwrap();
        dbg!(access_list);
    }
}
